  <!-- Game Section -->
  <section class="game-section">
  <div class="container game-container">
    <div class="game-banner">
      <picture>
        <source media="(max-width: 768px)" srcset="new1.png" >
        <source media="(min-width: 769px)" srcset="new.jpg">
        <img src="new.jpg" alt="Free Fire Banner" style="margin-left:5px ; border-radius:8px" />
      </picture>
    </div>
  </div>
</section>
  

<style>
@media (min-width:1024px){

  /* SECTION */
  section[style*="padding:6px"]{
    padding:40px 0 !important;
  }

  /* CONTAINER WIDTH */
  section > div{
    max-width:900px !important;
  }

  /* STEP NUMBER */
  section div[style*="clip-path"]{
    width:36px !important;
    height:36px !important;
    font-size:22px !important;
  }

  /* LOGIN HEADING */
  section div[style*="font-size:21px"]{
    font-size:30px !important;
  }

  /* CARD */
  section div[style*="background:#f7f7f7"]{
    padding:32px !important;
  }

  /* LABEL TEXT */
  section span[style*="font-size:16px"]{
    font-size:18px !important;
  }

  /* INPUT */
  section input{
    height:56px !important;
    font-size:18px !important;
  }

  /* LOGIN BUTTON */
  section button{
    height:56px !important;
    font-size:20px !important;
    padding:0 36px !important;
  }

  /* SOCIAL ICONS */
  section button img{
    transform:scale(1.15);
  }
}
</style>

  <!-- Login Section -->
  <section style="padding:6px 0; font-family: Arial, sans-serif;">
  <div style="max-width:900px; margin:auto; padding:0 16px;">

    <!-- Header -->
    <div style="display:flex; align-items:center; gap:7px; margin-bottom:8px;">
    <div style="
  width:24px;
  height:25px;
  background:#e11900;
  color:#fff;
  display:flex;
  align-items:center;
  justify-content:center;
  font-weight:700;
  font-size:18px;

  /* BOTTOM-RIGHT CUT */
  clip-path: polygon(
    0 0,
    100% 0,
    100% calc(100% - 8px),
    calc(100% - 8px) 100%,
    0 100%
  );
">
  1
</div>

      <div style="font-size:21px; font-weight:700; color:#000;">Login</div>
    </div>

    <!-- Card -->
    <div style="
      background:#f7f7f7;
      border:1px solid #e4e4e4;
      border-radius:16px;
      padding:16px;">

      <!-- Label -->
      <div style="margin-bottom:8px;">
        <span style="font-size:16px;color:#000; margin-left:9px">Player ID</span>
        <span style="
          margin-left: 0px;
    width: 16px;
    height: 17px;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    border: 2px solid #666;
    font-size: 14px;
    color: #666;
    font-weight: 600;
    vertical-align: middle;">
          ?
        </span>
      </div>

      <!-- Input + Button -->
      <div style="display:flex; margin-bottom:24px; flex-wrap:nowrap; gap:0;">
      
      <div style="flex:1; position:relative; min-width:200px; height:47px;">
        <input
          id="playerIdInput"
          name="unique_player_id_field_v3"
          autocomplete="off"
          readonly
          onfocus="this.removeAttribute('readonly');"
          placeholder=" "
          style="
            width:100%;
            height:47px;
            padding:0 20px;
            border:1px solid #ddd;
            border-radius:12px 0 0 12px;
            border-right: none;
            font-size:20px;
            color:#333;
            outline:none;
            background:transparent;
            position: absolute;
            top: 0;
            left: 0;
            z-index: 2;"/>
        
        <!-- Custom Placeholder Container -->
        <div class="custom-placeholder" style="
            position: absolute;
            top: 0;
            left: 20px;
            height: 100%;
            display: flex;
            align-items: center;
            pointer-events: none;
            color: #999;
            opacity: 0.5;
            z-index: 1;">
            <span style="white-space: nowrap;">Please enter player ID he</span>
            <i class="fas fa-angle-down input-dropdown-icon" style="margin-left: 5px;"></i>
        </div>
      </div>

<style>
/* Hide default placeholder */
input::placeholder {
  color: transparent;
}

/* Show custom placeholder when input is empty (placeholder shown) */
input:not(:placeholder-shown) + .custom-placeholder {
    display: none !important;
}

/* Mobile styles */
@media (max-width: 768px) {
    /* Reduce padding in Player ID section card */
    section div[style*="background:#f7f7f7"] {
        padding: 12px !important;
    }
    
    /* Reduce margin in label section */
    section div[style*="margin-bottom:8px"] {
        margin-bottom: 6px !important;
    }
    
    /* Reduce margin in input+button section */
    section div[style*="margin-bottom:24px"] {
        margin-bottom: 16px !important;
    }
    
    .input-dropdown-icon {
        display: inline-block !important; /* Show icon next to text */
    }
    
    /* Ensure input background is white behind the transparent input */
    #playerIdInput {
        background: #fff; 
    }
    
    /* When not focused/filled, make background transparent to show placeholder */
    #playerIdInput:placeholder-shown {
        background: transparent;
    }
    
    /* Container needs background since input is transparent */
    div[style*="position:relative"] {
        background: #fff;
        border-radius: 12px 0 0 12px;
    }

    /* Adjust padding and font size */
    #playerIdInput {
        padding-left: 10px !important;
        font-size: 17px !important; /* Increased font size */
    }
